import React from 'react';
//completedTab - Активная вкладка
class AppTab extends React.Component {
    constructor(props) {
        super(props);
        this.state = {done: false};
    }

    toggleTab = () => {
        this.setState((prevState) => {
            let tabs = document.querySelectorAll('.tabs input');
            for (let tab of tabs){
                tab.setState({done : false});
            }
            return {
                done: !prevState.done
            }
        });
    };

    render(){
        const { done } = this.state;
        let className = 'col-lg-4 tab';

        if (done){
            className += ' completedTab';
        }

        return (<input type="button" value = {this.props.name} className={className} data-id = {this.props.bool}
                       onClick={this.toggleTab}/>)
    }

}

export default AppTab;
