import React from 'react';
//completedTab - Активная вкладка
class AppTab extends React.Component {
    constructor(props) {
        super(props);
        this.state = {done: false};
    }

    completedTab = () => {
        let tabs = document.querySelectorAll('.tabs input');
        for (let tab of tabs){tab.classList.remove('completedTab')}
        this.setState = {done: true};
    };

    render(){
        let className = 'col-lg-4 tab';
        if (this.state.done){
            className += ' done';
        }
        return (<input type="button" value = {this.props.name} className={this.props.class} data-id = {this.props.bool}
                       onClick={this.completedTab}/>)
    }

}

export default AppTab;
