import React from 'react';
import Tab from '../app-tab'
import './tabs.css'

function AppTabs() {
    const nameTabs = [{name:'Активные',bool:true},{name:'Избранное',bool:false},{name:'Завершенные',bool:false}];
    const classTab = 'col-lg-4 tab';
    const classTabActive = 'completedTab col-lg-4 tab';

    return (<div className="add-items d-flex tabs">
        {nameTabs.map((tab, key) => {
            if (tab.bool === true) {
                return <Tab key={key} name={tab.name} data-id={tab.bool} class={classTabActive}/>
            } else {
                return <Tab key={key} name={tab.name} data-id={tab.bool} class={classTab}/>
            }
        })
        }
    </div>);
}
export default AppTabs
